layui Use From.body:
layui.use(['form'],function() {
 var form = layui.form;
  //监听提交
  form.on('submit(formDemo)', function(data) {
    layer.msg(JSON.stringify(data.field));
    return false;
  });
});

layui 弹出层.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.msg('Hello World');
});

layui 弹出层-带标题.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.msg('Hello World', {title: '标题'});
});

layui 弹出层-带图标.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.msg('Hello World', {icon: 1});
});

layui 弹出层-带标题和图标.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.msg('Hello World', {icon: 1, title: '标题'});
});

layui 弹出层-带标题和图标-红色.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.msg('Hello World', {icon: 1, title: '标题', bgcolor: 'red'});
});

layui 弹出层-询问.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.confirm('Hello World', function(index){
    lay.msg('点击了确定');
  });
});

layui 弹出层-询问-带标题.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.confirm('Hello World', function(index){
    lay.msg('点击了确定');
  }, '标题');
});

layui 弹出层-询问-多按钮.body:
layui.use(['layer'], function(){
 var layer = layui.layer;
  lay.confirm('Hello World', function(index){
    lay.msg('点击了确定');
  }, '标题', ['确定', '取消']);
});

layui 分页.body:
layui.use('laypage', function(){
var laypage = layui.laypage;

//执行一个laypage实例
laypage.render({
  elem: 'test1' //注意，这里的 test1 是 ID，不用加 # 号
  ,count: 50 //数据总数，从服务端得到
});
  });
