layui 表单提交-form.on.body:
layui.use(function(){
  var form = layui.form;
  var layer = layui.layer;

  // 监听表单提交事件
  form.on('submit(filter-name)', function(data){
    var field = data.field; // 获取表单全部字段值
    var elem = data.elem; // 获取当前触发事件的元素 DOM 对象
    var elemForm = data.form; // 获取当前表单域的 form 元素对象

    console.log(field); // 当前表单的字段值

    // 此处可执行 Ajax 提交
    // $.ajax({...});

    // 显示填写结果（仅作演示）
    layer.alert(JSON.stringify(field), {
      title: '表单提交的数据'
    });

    return false; // 阻止默认 form 跳转
  });

  // 监听 select 选择事件
  form.on('select(filter-name)', function(data){
    console.log(data.value); // 获取选中的值
    console.log(data.elem); // 获取当前 select 元素对象
  });

  // 监听 checkbox 复选框事件
  form.on('checkbox(filter-name)', function(data){
    console.log(data.value); // 开关 value 值
    console.log(data.elem.checked); // 是否被选中，true 或 false
  });

  // 监听 switch 开关事件
  form.on('switch(filter-name)', function(data){
    console.log(data.value); // 开关 value 值
    console.log(data.elem.checked); // 开关是否开启，true 或 false
  });

  // 监听 radio 单选框事件
  form.on('radio(filter-name)', function(data){
    console.log(data.value); // 获取选中的值
  });
});

