layui 弹出层-layer.open完整示例.body:
layui.use(function(){
  var layer = layui.layer;
  layer.open({
    type: 1, // page 层类型
    title: '标题',
    area: ['600px', '400px'], // 宽高
    shade: 0.6, // 遮罩透明度
    shadeClose: true, // 点击遮罩关闭
    maxmin: true, // 显示最大化最小化按钮
    anim: 0, // 弹出动画
    content: '<div style="padding: 20px;">这是内容区域</div>',
    btn: ['确定', '取消'], // 按钮
    yes: function(index, layero){
      // 点击确定按钮的回调
      layer.msg('点击了确定');
      layer.close(index);
    },
    btn2: function(index, layero){
      // 点击取消按钮的回调
      layer.msg('点击了取消');
      // return false; // 返回 false 可阻止关闭
    },
    success: function(layero, index){
      // 层弹出成功后的回调
      console.log('弹层已打开', layero, index);
    },
    end: function(){
      // 层销毁后的回调
      console.log('弹层已关闭');
    }
  });
});

